import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { RootState } from "../../redux/store/store";
import { useSelector } from "react-redux";
import { useEffect } from "react";
import LoginForm from "../sections/LoginForm";
import RegisterForm from "../sections/RegisterForm";

const LoginPage = () => {
  const login = useSelector((state: RootState) => state.login);
  const [signForm, setSignForm] = useState("login");

  const handleClick: React.MouseEventHandler<HTMLSpanElement> = (event) => {
    event.preventDefault();
    const name = event.currentTarget.getAttribute("id");
    if (name) {
      setSignForm(name);
    }
  };

  useEffect(() => {
    if (login.loggedIn) {
      window.location.href = "/home";
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [login]);

  return (
    <Container fluid className="pageContainer loginContainer">
      <Row>
        <Col className="col-6">
          <h2 className="mt-2">
            <span>
              <img
                src={
                  process.env.PUBLIC_URL +
                  "/visual/png/Pittogramma_light-green.png"
                }
                alt="logo"
                width={"15px"}
                className="me-3"
              />
            </span>
            Show your creative work
            <br />
            <span>
              <img
                src={
                  process.env.PUBLIC_URL +
                  "/visual/png/Pittogramma_light-green.png"
                }
                alt="logo"
                width={"15px"}
                className="me-3"
              />
            </span>
            Find other talents
            <br />
            <span>
              <img
                src={
                  process.env.PUBLIC_URL +
                  "/visual/png/Pittogramma_light-green.png"
                }
                alt="logo"
                width={"15px"}
                className="me-3"
              />
            </span>
            Start collaborating!
          </h2>
        </Col>
        <div id="loginBackground" />
        <Col className="authColumn">
          {signForm === "login" ? (
            <div className="w-100 text-center">
              <h2>Login</h2>
              <p>
                or{" "}
                <span onClick={handleClick} id="register" className="mazelink">
                  register
                </span>{" "}
                to join Maze!
              </p>
              <LoginForm />
            </div>
          ) : (
            <div className="w-100 text-center">
              <h2>Register</h2>
              <p>
                or{" "}
                <span onClick={handleClick} id="login" className="mazelink">
                  login
                </span>{" "}
                with your account
              </p>
              <RegisterForm />
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default LoginPage;
