import React, { useState, ChangeEvent } from "react";
import { Form, Button } from "react-bootstrap";
import { Profession } from "../../types/creativeType";

interface ProfessionsChoiceProps {
  selectedProfessions: Profession[];
  onChange: (selectedProfessions: Profession[]) => void;
}

const ProfessionsChoice: React.FC<ProfessionsChoiceProps> = ({
  selectedProfessions,
  onChange,
}) => {
  const [selectedProfession, setSelectedProfession] =
    useState<Profession | null>(null);

  const handleProfessionChange = (e: ChangeEvent<HTMLSelectElement>) => {
    const selectedProfessionName = e.target.value;
    const selectedProfession = Object.values(Profession).find(
      (profession) => profession.toString() === selectedProfessionName
    );
    setSelectedProfession(selectedProfession || null);
  };

  const handleAddProfession = () => {
    if (
      selectedProfession &&
      !selectedProfessions.includes(selectedProfession)
    ) {
      onChange([...selectedProfessions, selectedProfession]);
      setSelectedProfession(null);
    }
  };

  const handleRemoveProfession = (profession: Profession) => {
    const updatedProfessions = selectedProfessions.filter(
      (p) => p !== profession
    );
    onChange(updatedProfessions);
  };

  useEffect(() => {
    setSelectedProfession(null); // Resetta la professione selezionata quando selectedProfessions cambia
  }, [selectedProfessions]);

  return (
    <>
      <Form.Group controlId="formProfession">
        <Form.Label>Profession</Form.Label>
        <br />
        <Form.Select
          value={selectedProfession?.toString() || ""}
          onChange={handleProfessionChange}
        >
          <option value="">Select a profession</option>
          {Object.values(Profession).map((profession) => (
            <option key={profession} value={profession}>
              {profession}
            </option>
          ))}
        </Form.Select>
      </Form.Group>
      {selectedProfessions.length > 0 && (
        <div>
          <p>Selected professions:</p>
          <ul>
            {selectedProfessions.map((profession) => (
              <li key={profession}>
                {profession}
                <Button
                  variant="link"
                  onClick={() => handleRemoveProfession(profession)}
                >
                  Remove
                </Button>
              </li>
            ))}
          </ul>
        </div>
      )}
      {selectedProfessions.length < 3 && selectedProfession && (
        <Button variant="primary" onClick={handleAddProfession}>
          Add Profession
        </Button>
      )}
    </>
  );
};

export default ProfessionsChoice;
