import Button from "react-bootstrap/Button";
import { Col, Row, Form } from "react-bootstrap";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Dropdown from "react-bootstrap/Dropdown";
import { Modal } from "react-bootstrap";
import InputGroup from "react-bootstrap/InputGroup";
import { store } from "../../redux/store/store";
import {
  editFeedItem,
  deleteFeedItem,
} from "../../redux/actions/feedItemAction";
import { FeedItem } from "../../types/feedItemType";
import { useSelector } from "react-redux";
import { RootState } from "../../redux/store/store";
import { Creative, Profession } from "../../types/creativeType";

const FeedPost = ({
  id,
  collection,
  caption,
  type,
  author,
  createdAt,
}: FeedItem) => {
  const dispatch = store.dispatch;

  const session = useSelector((state: RootState) => state.login.session);

  const [editable, setEditable] = useState(false);
  const [showEdit, setShowEdit] = useState(false);
  const [showDelete, setShowDelete] = useState(false);
  const [expanded, setExpanded] = useState(false);
  const [profs, setProfs] = useState("");

  const handleShowEdit = () => setShowEdit(true);
  const handleCloseEdit = () => {
    setShowEdit(false);
  };

  const handleShowDelete = () => setShowDelete(true);
  const handleCloseDelete = () => {
    setShowDelete(false);
  };

  const [editFeedItemState, setEditFeedItemState] = useState({
    id,
    author,
    createdAt,
    caption,
    type,
    collection,
  });

  const handleSave = () => {
    dispatch(
      editFeedItem(
        session!.accessToken,
        editFeedItemState.id,
        editFeedItemState
      )
    );
    handleCloseEdit();
  };

  const toggleExpand = () => {
    setExpanded(!expanded);
  };

  const convertProfessions = (author: Creative) => {
    const professions = author.professions;
    professions.map(
      (profession: Profession) =>
        Profession[profession as keyof typeof Profession]
    );
  };

  useEffect(() => {
    if (session!.username === author.username) {
      setEditable(true);
    }

    if (caption.length < 200) {
      setExpanded(true);
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <Row className="postContainer">
      <Col xs={12}>
        <Row>
          <Col xs={2}>
            <Link to={`/profile/${editable ? "me" : author.username}`}>
              <img
                src={
                  author.image !== null
                    ? author.image
                    : "http://placekitten.com/300"
                }
                alt="Foto profilo"
                className="postProfileImage"
              />
            </Link>
          </Col>
          <Col className="postProfile">
            <Link to={`/profile/${editable ? "me" : author.username}`}>
              <p className="stagename mazeLink">{author.stageName}</p>
            </Link>
            <p>{author.professions.join(", ")}</p>
            <p>
              {createdAt?.slice(0, 10)} â€¢ {type}
            </p>
          </Col>
          <Col xs={1}>
            {editable && (
              <Dropdown className="d-inline mx-2 p-0">
                <Dropdown.Toggle
                  id="dropdown-autoclose-true"
                  className="p-0 unstyledDropdown"
                >
                  ...
                </Dropdown.Toggle>

                <Dropdown.Menu>
                  <Dropdown.Item onClick={handleShowEdit}>
                    <i className="bi bi-pencil"></i>
                  </Dropdown.Item>
                  <Dropdown.Item onClick={handleShowDelete}>
                    <i className="bi bi-trash"></i>
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            )}

            <Modal show={showDelete} onHide={handleCloseDelete}>
              <Modal.Header closeButton>
                <Modal.Title>Sei sicuro di eliminare questo Post?</Modal.Title>
              </Modal.Header>
              <Modal.Footer>
                <Button
                  variant="secondary"
                  onClick={() => setShowDelete(false)}
                >
                  Close
                </Button>
                <Button
                  variant="danger"
                  onClick={() => {
                    handleCloseDelete();
                    dispatch(deleteFeedItem(id!, session!.accessToken));
                  }}
                >
                  Delete
                </Button>
              </Modal.Footer>
            </Modal>

            <Modal show={showEdit} onHide={handleCloseEdit}>
              <Modal.Header closeButton>
                <Modal.Title>Edit Post</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <InputGroup>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    value={editFeedItemState.caption}
                    onChange={(e) =>
                      setEditFeedItemState({
                        ...editFeedItemState,
                        caption: e.target.value,
                      })
                    }
                    placeholder="Your caption here..."
                  />
                </InputGroup>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleCloseEdit}>
                  Close
                </Button>
                <Button
                  variant="primary"
                  disabled={editFeedItemState ? false : true}
                  onClick={handleSave}
                >
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal>
          </Col>
        </Row>
        <Row className="postText">
          <p className={expanded ? "expanded" : "collapsed"}>{caption}</p>
          {!expanded && (
            <p className="mazeLink text-center w-100" onClick={toggleExpand}>
              Mostra tutto
            </p>
          )}
        </Row>
        <Row
          className={
            collection.coverImage === undefined
              ? "text-center mb-2 d-none"
              : "text-center mb-2 d-block"
          }
        >
          <Link to={`/collection/${collection.id}`}>
            <img
              className="w-100"
              src={collection.coverImage}
              alt="Collection cover"
            />
          </Link>
        </Row>
        <hr />
        <Row className="btnSection text-center">
          <Button className="btnGreenLight w-100 m-0">
            <i className="bi bi-send-fill"></i>
            <span> Share</span>
          </Button>
        </Row>
      </Col>
    </Row>
  );
};

export default FeedPost;
