import {
  CreativeActionType,
  CreativeAction,
  CreativeState,
} from "../types/creativeType";

const initialState: CreativeState = {
  creative: {
    id: 0,
    username: "",
    email: "",
    password: "",
    roles: [],
    createdAt: "",
    updatedAt: null,
  },
  loading: false,
  error: null,
};

const creativeReducer = (
  state: CreativeState = initialState,
  action: CreativeAction
): CreativeState => {
  switch (action.type) {
    case CreativeActionType.GET_ZENYTE_REQUEST:
      return {
        ...state,
        loading: true,
        error: null,
      };
    case CreativeActionType.GET_ZENYTE_SUCCESS:
      return {
        ...state,
        creative: action.payload,
        loading: false,
        error: null,
      };
    case CreativeActionType.GET_ZENYTE_FAILURE:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export default creativeReducer;
