import {
  FeedItemActionType,
  FeedItemAction,
  FeedItemState,
} from "../types/feedType";

const initialState: FeedItemState = {
  feedItem: {
    id: 0,
    author: "",
    createdAt: "",
    collection: {
      id: 0,
      createdAt: "",
      updatedAt: "",
      title: "",
      description: "",
      category: "",
      coverImage: "",
      elaborates: [],
      keywords: [],
      singleElement: false,
      author: "",
    },
    type: "",
    caption: "",
  },
  loading: false,
  error: null,
};

const feedReducer = (
  state = initialState,
  action: FeedItemAction
): FeedItemState => {
  switch (action.type) {
    case FeedItemActionType.GET_FEED_REQUEST:
    case FeedItemActionType.UPDATE_FEED_REQUEST:
      return {
        ...state,
        feed: null,
        loading: true,
        error: null,
      };
    case FeedItemActionType.GET_FEED_SUCCESS:
    case FeedItemActionType.UPDATE_FEED_SUCCESS:
      return {
        ...state,
        feed: action.payload,
        loading: false,
        error: null,
      };
    case FeedItemActionType.GET_FEED_FAILURE:
    case FeedItemActionType.UPDATE_FEED_FAILURE:
      return {
        ...state,
        loading: false,
        // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
        error: action.error!,
      };
    default:
      return state;
  }
};

export default feedReducer;
