import { useEffect, useState } from "react";
import { Navbar, Nav, Form, FormControl, Container } from "react-bootstrap";
import { Link, useLocation } from "react-router-dom";
import ResearchBar from "./ResearchBar";

const MazeNavbar = () => {
  const location = useLocation();
  const [currentPage, setCurrentPage] = useState("");
  const isLoginPage = location.pathname === "/";

  const [isMdScreen, setIsMdScreen] = useState(window.innerWidth >= 768);
  const [isLgScreen, setIsLgScreen] = useState(window.innerWidth >= 1200);

  useEffect(() => {
    const handleResize = () => {
      setIsLgScreen(window.innerWidth >= 1200);
      setIsMdScreen(window.innerWidth >= 768);
    };

    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    if (location.pathname === "/portfolio/me") {
      setCurrentPage("portfolio");
    } else if (location.pathname === "/feed") {
      setCurrentPage("feed");
    }
  }, [location]);

  return (
    <Navbar expand="md" className="mazeNavbar">
      <Container fluid>
        <Navbar.Brand as={Link} to="/home">
          <img
            className="navbarLogo"
            src={process.env.PUBLIC_URL + "/visual/png/Logo_on_dark.png"}
            alt="logo"
          />
        </Navbar.Brand>
        {isLoginPage ? (
          <ResearchBar />
        ) : (
          <>
            <Navbar.Toggle
              aria-controls="maze-nav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="bi bi-list" />
            </Navbar.Toggle>
            <Navbar.Collapse id="maze-nav">
              <Nav className="m-auto">
                <Nav.Item>
                  <Nav.Link as={Link} to="/portfolio">
                    Portfolio
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link as={Link} to="/feed">
                    Feed
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <ResearchBar />
                </Nav.Item>
              </Nav>
            </Navbar.Collapse>
          </>
        )}
      </Container>
    </Navbar>
  );
};

export default MazeNavbar;
