import { FormEvent, useRef, useState } from "react";
import {
  Container,
  Row,
  Col,
  Form,
  Button,
  Image,
  Spinner,
} from "react-bootstrap";
import { RootState, resetStoreAction, store } from "../../redux/store/store";
import loginFetch from "../../redux/actions/loginAction";
import { useSelector } from "react-redux";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getCreative } from "../../redux/actions/creativeAction";
import LoginForm from "../sections/LoginForm";

const LoginPage = () => {
  const dispatch = store.dispatch;
  const navigate = useNavigate();
  const login = useSelector((state: RootState) => state.login);
  const creative = useSelector((state: RootState) => state.me);
  const formRef = useRef<HTMLFormElement>(null);
  const [formValues, setFormValues] = useState({
    username: "",
    password: "",
  });

  const handleChange = (e: { target: { name: string; value: string } }) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    const loadData = async () => {
      await dispatch(loginFetch(formValues.username, formValues.password));
    };
    e.preventDefault();
    loadData();
  };

  useEffect(() => {
    if (me.creative?.id) {
      navigate("/home");
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [creative]);

  useEffect(() => {
    const loadCreative = async () => {
      await dispatch(getCreative(login.session.username));
    };
    if (login.loggedIn) {
      loadCreative();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [login]);

  useEffect(() => {
    dispatch(resetStoreAction);
    if (formRef.current) {
      formRef.current.reset();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <Container fluid>
      <Row>
        <Col>
          <h1 className="text-primary mb-2">Welcome</h1>
          <h2 className="text-secondary small mb-3">
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Autem ut
            dicta sequi omnis sint
          </h2>
        </Col>
        {login.loading ? (
          <Col>
            <Spinner variant="primary" animation="border" />
          </Col>
        ) : (
          <Col>
            <LoginForm />
          </Col>
        )}
      </Row>
    </Container>
  );
};

export default LoginPage;
