import { FeedActionType, FeedAction, FeedState } from "./../types/feedType";

const initialState: FeedState = {
  feed: {
    id: 0,
    title: "",
    description: "",
    createdAt: "",
    updatedAt: null,
    owner: {
      id: 0,
      username: "",
      email: "",
      password: "",
      roles: [],
      createdAt: "",
      updatedAt: null,
    },
    articles: [],
  },
  loading: false,
  error: null,
};

const feedReducer = (state = initialState, action: FeedAction): FeedState => {
  switch (action.type) {
    case FeedActionType.GET_FEED_REQUEST:
    case FeedActionType.UPDATE_FEED_REQUEST:
      return {
        ...state,
        feed: null,
        loading: true,
        error: null,
      };
    case FeedActionType.GET_FEED_SUCCESS:
    case FeedActionType.UPDATE_FEED_SUCCESS:
      return {
        ...state,
        feed: action.payload,
        loading: false,
        error: null,
      };
    case FeedActionType.GET_FEED_FAILURE:
    case FeedActionType.UPDATE_FEED_FAILURE:
      return {
        ...state,
        loading: false,
        // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
        error: action.error!,
      };
    default:
      return state;
  }
};

export default feedReducer;
