import {
  PortfolioActionType,
  PortfolioAction,
  PortfolioState,
} from "./../types/portfolioType";

const initialState: PortfolioState = {
  portfolio: {
    id: 0,
    title: "",
    description: "",
    createdAt: "",
    updatedAt: null,
    owner: {
      id: 0,
      username: "",
      email: "",
      password: "",
      roles: [],
      createdAt: "",
      updatedAt: null,
    },
    articles: [],
  },
  loading: false,
  error: null,
};

const portfolioReducer = (
  state = initialState,
  action: PortfolioAction
): PortfolioState => {
  switch (action.type) {
    case PortfolioActionType.GET_BLOG_REQUEST:
    case PortfolioActionType.UPDATE_BLOG_REQUEST:
      return {
        ...state,
        portfolio: null,
        loading: true,
        error: null,
      };
    case PortfolioActionType.GET_BLOG_SUCCESS:
    case PortfolioActionType.UPDATE_BLOG_SUCCESS:
      return {
        ...state,
        portfolio: action.payload,
        loading: false,
        error: null,
      };
    case PortfolioActionType.GET_BLOG_FAILURE:
    case PortfolioActionType.UPDATE_BLOG_FAILURE:
      return {
        ...state,
        loading: false,
        // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
        error: action.error!,
      };
    default:
      return state;
  }
};

export default portfolioReducer;
