import React, { useState } from "react";
import { Form, Button, Container } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "../../store/store";
import loginFetch from "../../actions/loginAction";

const LoginPage: React.FC = () => {
  const dispatch = useDispatch();
  const { loading, error } = useSelector((state: RootState) => state.login);

  let loginDto = {
    username: "",
    password: "",
  };

  let token;

  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  const handleSubmit = (event: { preventDefault: () => void }) => {
    event.preventDefault();
    dispatch(loginFetch(loginDto.username, loginDto.password));
  };

  return (
    <Container>
      <h1>Login</h1>
      <Form onSubmit={handleSubmit} className="mt-5">
        <Form.Group className="mb-3 w-25 mx-auto" controlId="username">
          <Form.Control required type="text" placeholder="insert username.." />
        </Form.Group>
        <Form.Group className="mb-3 w-25 mx-auto" controlId="password">
          <Form.Control
            required
            type="password"
            placeholder="insert password.."
          />
        </Form.Group>
        {error && <div className="text-danger">{error}</div>}
        <Button
          type="submit"
          className="btn btn-dark btn-outline-warning my-2 w-25 "
        >
          Login
        </Button>
      </Form>
    </Container>
  );
};

export default LoginPage;
