import React, { useState, ChangeEvent, useEffect } from "react";
import { Form, Button } from "react-bootstrap";
import { Skill } from "../../types/creativeType";

interface SkillsChoiceProps {
  selectedSkills: Skill[];
  onChange: (selectedSkills: Skill[]) => void;
}

const SkillsChoice: React.FC<SkillsChoiceProps> = ({
  selectedSkills,
  onChange,
}) => {
  const [selectedSkill, setSelectedSkill] = useState<Skill | null>(null);

  const handleSkillChange = (e: ChangeEvent<HTMLSelectElement>) => {
    const selectedSkillName = e.target.value;
    const selectedSkill = Object.values(Skill).find(
      (skill) => skill.toString() === selectedSkillName
    );
    setSelectedSkill(selectedSkill || null);
  };

  const handleAddSkill = () => {
    if (selectedSkill && !selectedSkills.includes(selectedSkill)) {
      onChange([...selectedSkills, selectedSkill]);
      setSelectedSkill(null);
    }
  };

  const handleRemoveSkill = (skill: Skill) => {
    const updatedSkills = selectedSkills.filter((s) => s !== skill);
    onChange(updatedSkills);
  };

  useEffect(() => {
    setSelectedSkill(null); // Resetta la skill selezionata quando selectedSkills cambia
  }, [selectedSkills]);

  return (
    <>
      <Form.Group controlId="formSkill">
        <Form.Label>Skill</Form.Label>
        <br />
        <Form.Select
          value={selectedSkill?.toString() || ""}
          onChange={handleSkillChange}
        >
          <option value="">Select a skill</option>
          {Object.values(Skill).map((skill) => (
            <option key={skill} value={skill}>
              {skill}
            </option>
          ))}
        </Form.Select>
      </Form.Group>
      {selectedSkills.length > 0 && (
        <div>
          <p>Selected skills:</p>
          <ul>
            {selectedSkills.map((skill) => (
              <li key={skill}>
                {skill}
                <Button variant="link" onClick={() => handleRemoveSkill(skill)}>
                  Remove
                </Button>
              </li>
            ))}
          </ul>
        </div>
      )}
      {selectedSkills.length < 10 && selectedSkill && (
        <Button variant="primary" onClick={handleAddSkill}>
          Add Skill
        </Button>
      )}
    </>
  );
};

export default SkillsChoice;
