import { Container, Row, Col, Image, Button } from "react-bootstrap";
import { useSelector } from "react-redux";
import { RootState, store } from "../../redux/store/store";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import MeSection from "../sections/MeSection";
import { getFeed } from "../../redux/actions/feedItemAction";

function FeedPage() {
  const navigate = useNavigate();
  const dispatch = store.dispatch;
  const me = useSelector((state: RootState) => state.me.creative);
  const login = useSelector((state: RootState) => state.login);
  const feed = useSelector((state: RootState) => state.feedItem.feed.feedItems);

  useEffect(() => {
    if (!login.loggedIn) {
      navigate("/");
    } else {
      dispatch(getFeed(login.session!.accessToken));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <Container fluid className="pageContainer">
      <Row>
        <Col xs={12} md={3} lg={4}>
          <MeSection />
        </Col>
        <Col className="feedCol">
          <Row>
            <p>From your followed creatives:</p>
          </Row>
          <Row className="feedRow">
            {feed.length === 0 ? (
              <h3>
                Seems like you haven't any updates. Follow more creatives or
                come back later!
              </h3>
            ) : (
              <></>
            )}
          </Row>
        </Col>
      </Row>
    </Container>
  );
}

export default FeedPage;
